mapper.select:
		select count(*) from t_commodity 
		
		commodityisman = #{commodityisman}
			
  				and commodityPrice > #{low}
  			
  			
  				and commodityPrice <= #{high}

	select * from (select t.*,rownum rownu from t_commodity t 
        where rownum<=#{pageSize} and commodityisman = #{commodityisman})tt
        where tt.rownu>#{startIndex}

	select * from (select t.*,rownum rownu from t_commodity t 
        where rownum<=#{pageSize} and commodityisman = #{commodityisman} and commodityPrice > #{low} and commodityPrice <= #{high})tt
        where tt.rownu>#{startIndex}

	select * from (select t.*,rownum rownu from t_commodity t,t_gategories g
        where rownum<=#{pageSize} and commodityisman = #{commodityisman} and commodityPrice > #{low} and commodityPrice <= #{high} and g.gategoriesName=#{gategoriesName} and t.gategoriesid=g.gategoriesid )tt
        where tt.rownu>#{startIndex}

	select * from (select t.*,rownum rownu from t_commodity t,t_gategories g
        where rownum<=#{pageSize} and commodityisman = #{commodityisman} and g.gategoriesName=#{gategoriesName} and t.gategoriesid=g.gategoriesid)tt
        where tt.rownu>#{startIndex}
